changeSet.sql:
create table customer ( id varchar(100) not null primary key, name varchar(255), surname varchar(255), patronymic varchar(255), phoneNumber varchar(255) );;
create table customer_address ( id varchar(100) not null primary key, customerId varchar(100) references customer(id), displayAddress varchar(2000), longitude numeric, latitude numeric );;
create table employee ( id varchar(100) not null primary key, name varchar(255), surname varchar(255), patronymic varchar(255), deviceId varchar(100) );;
create table clean_order ( id varchar(100) not null primary key, customerId varchar(100) references customer(id), number varchar(30), pickUpAddress varchar(100) references customer_address(id), deliveryAddress varchar(100) references customer_address(id), pickUpDate timestamp, deliveryDate timestamp, cleanStatus varchar(30), totalOrderPrice numeric, assignedToPickUp varchar(100) references employee(id), assignedToDeliver varchar(100) references employee(id), pickedUpBy varchar(100) references employee(id), deliveredBy varchar(100) references employee(id) );;
create table application_dict ( code varchar(100) not null primary key, title varchar(100), dictType varchar(30) );;
create table dict_parent_relation ( parentCode varchar(100) not null, childCode varchar(100) not null, primary key(parentCode, childCode) );;
create table clean_service ( id varchar(100) not null primary key, cleanOrderId varchar(100) references clean_order(id), merchantCount varchar(100) references application_dict(code), merchantType varchar(100) references application_dict(code), merchantMaterial varchar(100) references application_dict(code), merchantMeasurement varchar(100) references application_dict(code), merchantService varchar(100) references application_dict(code), merchantWidth numeric, merchantHeight numeric, servicePrice numeric );;
create sequence s_order_number increment by 1 start 1;;
